INSERT INTO theme_items (theme_id, user_id, content, list, created_at, updated_at) VALUES
('11111111-1111-1111-1111-111111111101', '22222222-2222-2222-2222-222222222201', '日本の絶景スポット', '<ul><li>京都の紅葉スポット</li><li>沖縄のビーチリゾート</li><li>富士山の眺望ポイント</li><li>北海道の雪景色</li><li>瀬戸内海の島々</li></ul>', NOW() - INTERVAL '55 days', NOW() - INTERVAL '55 days'),
('11111111-1111-1111-1111-111111111101', '22222222-2222-2222-2222-222222222202', 'ヨーロッパ旅行おすすめ都市', '<ul><li>パリ - エッフェル塔とルーブル美術館</li><li>ローマ - コロッセオとバチカン市国</li><li>バルセロナ - サグラダファミリア</li><li>アムステルダム - 運河と美術館</li><li>プラハ - 中世の街並み</li></ul>', NOW() - INTERVAL '50 days', NOW() - INTERVAL '50 days'),
('11111111-1111-1111-1111-111111111101', '22222222-2222-2222-2222-222222222203', '旅行の持ち物チェックリスト', '<ul><li>パスポートと航空券</li><li>現地通貨と国際クレジットカード</li><li>充電器とアダプター</li><li>常備薬と救急セット</li><li>旅行保険の証書</li></ul>', NOW() - INTERVAL '45 days', NOW() - INTERVAL '45 days'),
('11111111-1111-1111-1111-111111111101', '22222222-2222-2222-2222-222222222204', 'アジア旅行おすすめスポット', '<ul><li>バリ島 - ビーチとライステラス</li><li>バンコク - 寺院と水上マーケット</li><li>ソウル - 宮殿と韓国料理</li><li>香港 - 夜景と飲茶</li><li>シンガポール - マリーナベイサンズとガーデンズ</li></ul>', NOW() - INTERVAL '40 days', NOW() - INTERVAL '40 days');
